export const convoStateReducer = (convoState = {}, action) => {

    switch(action.type) {

        case 'NO_CONVO':

            return 'NoConvo';

        case 'YES_CONVO':

            return 'YesConvo';

        case 'SEARCHING_CONVO':

            console.log(action.payload);

            return action.payload;

        default:
            return convoState;
     }

 }
