import { DotsVerticalIcon} from "@heroicons/react/outline";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import{faHandPointUp, faComments, faShareSquare} from "@fortawesome/free-solid-svg-icons";

function PostBox() {
    return (
        // Post-Box
        <div className="mx-2 my-1"> 

            {/* Post-Top-Cyan Invisible Parent*/}
            <div className="flex  justify-center">
                {/* Top-Cyan */}
                <div className="bg-gradient-to-r from-teal-300 to-blue-400  w-full sm:w-1/3 rounded-l-full transition delay-50 py-1 flex items-center font-bold shadow-xl justify-between">
                        {/*Top-Mid*/}
                        <div className="flex m-auto items-center justify-between">
                            {/* Top-Mid Img*/}
                            <div className="ml-9 bg-white rounded-full">
                                <img src="./assets/images/milome.jpeg" alt="DP" className=" rounded-full h-10 w-10 m-0.5"/>
                            </div> 
                            {/*Top-Mid-Words */}
                            <div className="ml-1 sm:ml-2 items-center ml-1 py-3"> 
                                <p className="leading-3 text-sm font-medium my-1 ">Felix Milome</p>
                                <p className="leading-3 text-xs font-light">Social Post: 50 Mins ago</p>

                            </div>
                        </div>
                    {/* Top-Menu */}
                    <div>
                    <DotsVerticalIcon className=" h-7 pr-3 items-center cursor-pointer hover:text-cyan-300 text-white"/>
                    </div>

                </div> 

            </div>

        {/* Post Caption Invisible Parent */}
        <div className="flex justify-center mt-0.5">
            {/* Post Mid Frame*/}
            <div className="w-full sm:w-1/3 items-center p-5 bg-white rounded-t-xl"> 
                                <p className="leading-5 text-base font-medium my-1 text-gray-500">How I became a Billionaire</p>
                                <p className="leading-5 text-sm font-light text-gray-700">I was born to become one but I also worked for it. Always stay focussed and positive for one day all will go well for you. Never give up in whatever you are pursuing</p>                 
            </div>  
        </div>

        {/* Post Mid Invisible Parent */}
        <div className="flex justify-center">
            {/* Post Mid Frame*/}
            <div className="w-full sm:w-1/3 transition delay-50 flex p-1 items-center cursor-pointer bg-white font-bold hover:bg-cyan-400">
                <img src="./assets/images/khabilame.jpeg" alt="DP" className="h-full w-full"/>  
            </div>     
        </div>

        {/*Bottom Icon invisible Parent*/}
        <div className="flex justify-center">
            {/* Post Bottom Icons*/}
            <div className="w-full sm:w-1/3 justify-around rounded-b-xl transition delay-50 flex items-center  bg-white font-bold px-10">
                <div className="flex items-center rounded-full p-1 cursor-pointer hover:bg-gray-200">
                    <FontAwesomeIcon icon={faHandPointUp } size ={'2x'} className="text-yellow-400 p-1" />
                    <p className="font-light text-xs m-1 text-gray-800">30M Points</p>
                </div>
                <div className="flex items-center rounded-full p-1 cursor-pointer hover:bg-gray-200">
                    <FontAwesomeIcon icon={faComments} size ={'2x'} className="text-blue-400 p-1.5"/>
                    <p className="font-light text-xs m-1 text-gray-800">500K Opinions</p>
                </div>
                <div className="flex items-center p-1 rounded-full cursor-pointer hover:bg-gray-200">
                    <FontAwesomeIcon icon={faShareSquare} size ={'2x'} className="text-green-400 p-1.5"/>
                    <p className="font-light text-xs m-1 text-gray-800">400M displays</p>
                </div>
            </div>     
        </div>
    </div>
    )
}

export default PostBox;
