export const convoStateReducer = (convoState = {message:'SearchingConvo'}, action) => {

    switch(action.type) {
        case 'NO_CONVO':
            return 'NoConvo';
        case 'YES_CONVO':
            return 'YesConvo';
        default:
            return convoState;
     }

 }
