import { DotsVerticalIcon, PlusIcon } from "@heroicons/react/outline";

function Posts() {
    return (
        // Post-Box
       <div className="m-2"> 

            {/* Post-Top-Cyan Invisible Parent*/}
            <div className="flex  justify-center">
                {/* Top-Cyan */}
                <div className="bg-gradient-to-r from-teal-400 to-blue-500  w-full sm:w-1/3 rounded-l-full transition delay-50 flex  py-2 flex items-center cursor-pointer font-bold  shadow-xl">
                        {/*Top-Mid*/}
                        <div className="flex m-auto items-center">
                            {/* Top-Mid Img*/}
                            <div className="ml-9 bg-white rounded-full">
                                <img src="./assets/images/milome.jpeg" alt="DP" className=" rounded-full h-10 w-10 m-0.5"/>
                            </div> 
                            {/*Top-Mid-Words */}
                            <div className="ml-1 sm:ml-2 items-center ml-1 py-3"> 
                                <p className="leading-3 text-base font-medium">Felix Milome</p>
                                <small className="leading-3 text-xs font-light">Social Post: 52 mins ago</small>
                            </div>
                        </div>
                    {/* Top-Menu */}
                    <div>
                    <DotsVerticalIcon className="mr-4 h-7 items-center text-white"/>
                    </div>

                </div> 

            </div>

        {/* Post Mid Invisible Parent */}
        <div className="flex justify-center mt-0.5">
            {/* Post Mid Frame*/}
            <div className="w-full sm:w-1/3 justify-center transition delay-50 flex p-1 items-center cursor-pointer bg-white font-bold  hover:bg-cyan-400">
            <img src="./assets/images/milome.jpeg" alt="DP" className="h-full w-full"/>  
            </div>     
        </div>

        <div className="flex justify-center mt-0.5">
            {/* Post Mid Frame*/}
            <div className="w-full sm:w-1/3 justify-center transition delay-50 flex p-5 items-center cursor-pointer bg-white font-bold  hover:bg-cyan-400">
            <p></p>
            </div>     
        </div>

    </div>


    )
}

export default Posts;
