import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import{faPrayingHands} from "@fortawesome/free-solid-svg-icons";

function ReviewBubble({Src,Name,Review}) {
    return (



        <div className="flex w-full justify-start items-center bg-red-100 text-xs font-bold text-gray-700 rounded-md lg:max-w-none m-1">

           {/* EMoji & Pic */}
            <div className="bg-green-300 items-center">
                <img src={Src} alt="DP" className="p-0.5 rounded-full object-cover h-8 w-8"/>
                <div className="bg-gray-100 rounded-full flex justify-center cursor-pointer h-7 w-7 items-center hover:bg-cyan-400 group ">
                    <FontAwesomeIcon icon={faPrayingHands} className="text-gray-400 group-hover:text-white" />
                </div>
            </div>
            {/* Name and Comment*/}
            <div className="m-1 bg-gray-300 p-3 rounded-xl max-w-xs">  
                <p> {Name} </p>
                <div className= "font-light break-words">{Review}</div>
            </div>

        </div>



    )
}

export default ReviewBubble;
