import {useSelector, useDispatch} from 'react-redux';
import { useEffect, useState} from 'react';
import { getReviewsAction } from '../Midwares/rdx/actions/reviewsAction';
import moment from 'moment'; 
import{GiMoneyStack, GiTakeMyMoney} from "react-icons/gi"; 
import { BiCommentEdit } from "react-icons/bi";
import { MdSend,MdOutlineCancel} from "react-icons/md";
import {BeatLoader} from "react-spinners";
import {patchReviewsAction} from "../Midwares/rdx/actions/reviewsAction.js"

export const PostFrameRevRow = ({diaryId, userId, reviewer}) => {

     const[reviewData, setreviewData] = useState({reviewedPostId:'', body: ''});
      const[reviewLoading, setReviewLoading] = useState(false);
      const[reviewDelivery, setReviewDelivery] = useState(false); 
       const[reviewEditor, setReviewEditor] = useState(false); 




    const dispatch = useDispatch(); 





    const patchDiary = () =>{

        setReviewLoading(true);

        try{
            dispatch(patchReviewsAction(reviewData, setreviewData, setReviewLoading, setReviewDelivery));
            console.log(reviewData);
        }
        catch(error){
            console.log(error);
        }

    }


    return (



                <div key={reviewer._id}  className='bg-gray-100 border-gray-300 rounded-md '>


                    {/* EDITOR */}
                    {reviewEditor && <div className="absolute w-1/2 items-center">

                           <div  className='absolute bottom-3 right-3'>
                                {reviewData && reviewData.body.length > 0 && reviewData.body.length < 2000 &&
                                <>
                                    {!reviewLoading &&
                                    <div className='flex'>
                                    <p className= 'text-xs p-0.5'> edit</p>
                                     <MdSend onClick= {patchDiary}/> 
                                     </div>
                                     }
                                    {reviewLoading && 
                                    <>
                                    {/* <p className= 'text-xs font-extralight'>sending review</p> */}
                                    <BeatLoader size={7} color='black' loading/>
                                    </> }
                                </>
                                } 
                            </div>



                            <textarea value= {reviewData.body}
                            onChange={(e)=> setreviewData({patchedReviewId:reviewer._id, body: e.target.value})}
                            type="text" placeholder="Write Review Here..." className="max-h-screen w-full text-gray-700 font-light outline-none bg-gray-100 text-sm  border border-gray-300 rounded-md py-3 pl-3 pr-8"/>

                    </div>
                    }


                    <div className="p-0.5 flex w-full justify-start items-center text-xs font-bold text-gray-600 rounded-md lg:max-w-none">

                            {/* EMoji & Pic */}
                            <div className="space-y-3 items-center inline-block p-1">

                                <img src={reviewer.reviewerMiniProfile.dpUrl} alt="DP" className="rounded-full object-cover h-8 w-8"/>

                            </div>

                            {/* Name and Comment*/}
                            <div className="flex items-center m-1 bg-transparent w-5/6">  
                            <div className="bg-gray-100 border  border-gray-300 p-3 rounded-2xl max-w-3/4">

                                    <div className= 'bg-transpparent rounded-md'>


                                        <p className='text-xs font-semibold'> @{reviewer.reviewerMiniProfile.userName}:</p>

                                    </div>

                                <div className= "font-light text-sm break-words">{reviewer.body}</div>
                                <p className='font-extralight'>{moment (reviewer.time).fromNow()}</p>
                                </div>
                                <div className="bg-transparent rounded-full flex justify-center cursor-pointer h-7 w-7 items-center hover:bg-white group m-3">
                                    <GiTakeMyMoney size ={24} className="text-gray-400"/>
                                </div>
                                <div onClick= {()=>{
                                    setreviewData({...reviewData, body:reviewer.body});
                                    setReviewEditor(true);
                                    }} className="bg-transparent rounded-full flex justify-center cursor-pointer h-7 w-7 items-center hover:bg-white group m-3">
                                    <BiCommentEdit  size ={20} className="text-gray-400"/>
                                </div>
                            </div>

                    </div>


            </div>

    )
}
