import {SearchIcon} from '@heroicons/react/outline';
import { useState } from 'react';
import {BsPeople, BsFillFileEarmarkPostFill} from 'react-icons/bs';


export const Search = () => {
 const [searchOptions, setSearchOptions] = useState(false);

  return (

    <div className='flex items-center'>

        <div className="p-1 sm:p-3 bg-cyan-400 hover:bg-cyan-600 rounded-full items:center">
            <SearchIcon onClick={()=> setSearchOptions(!searchOptions)} className= 'h-6 text-white'/>

        </div>
        <input className ="bg-transarent w-full h-7 md:inline-flex pl-2 pr-4 bg-transparent items-center outline-none font-light placeholder-gray-400"
        type="text"
        placeholder="Search Zoorura"/>

    {searchOptions &&
        <div className= 'fixed space-y-2 top-24 sm:top-16 text-sm text-gray-500 bg-cyan-500 z-40 h-fit w-fit rounded-md p-2'>
            <div className= 'flex items-center space-x-2 px-3 py-1 rounded-md hover:shadow-md hover:bg-gray-100 bg-gray-100 cursor-pointer'>
                <div><BsPeople size={20}/></div>
                <p>Search People</p>      
            </div>
            <div className= 'flex items-center space-x-2 px-3 py-1 rounded-md hover:shadow-md hover:bg-gray-100 bg-gray-100 cursor-pointer'>
                <div><BsFillFileEarmarkPostFill size={20}/></div>
                <p>Search People</p>      
            </div>

        </div>}



    </div>

  )
}
