import * as axs from './axs'; 


export const headSearchAction = (searchData, setLoading, setError) => async (dispatch) =>{
    try{
        console.log(searchData);
        const{data}= await axs.headSearchApi(searchData);
        const {diaryResult, peopleResult, message} = data;
            const allResults = peopleResult.concat(diaryResult);
            console.log(data);
            console.log(data.message);

        if(data.message === 'Success'){

            dispatch({type: 'HEAD_SEARCH', payload: allResults});

            setLoading(false);

        } else if (message === 'NoTerm'){

            setLoading(false);
            setError('NoTerm');


        } else if (message === 'NoPerson'){

            setLoading(false);
            setError('NoPerson');

        } else if (message === 'NoPost'){

            setLoading(false);
            setError('NoPost');

        }  


    } catch(error){
        console.log(error.message);
    }
}
