import { DotsVerticalIcon, PlusIcon } from "@heroicons/react/outline";

function Posts() {
    return (
        // Post-Box
       <div className="m-1"> 

            {/* Post-Top-Cyan Invisible Parent*/}
            <div className="flex justify-center">
                {/* Top-Cyan */}
                <div className=" w-full sm:w-1/2  transition delay-50 flex  py-2 px-4 flex items-center cursor-pointer font-bold  bg-cyan-400 shadow-xl">
                        {/*Top-Mid*/}
                        <div className="absolute flex m-auto items-center">
                            {/* Top-Mid Img*/}
                            <div className="bg-white rounded-full">
                                <img src="./assets/images/milome.jpeg" alt="DP" className=" rounded-full h-12 w-12 m-0.5"/>
                            </div> 
                            {/*Top-Mid-Words */}
                            <div className="items-center ml-1 py-3"> 
                                <p className="leading-3 text-base font-medium">Felix Milome</p>
                                <small className="leading-3 text-xs font-light">52 mins ago</small>
                            </div>
                        </div>
                    {/* Top-Menu */}
                    <div>
                    <DotsVerticalIcon className="h-6 text-white"/>
                    </div>
                    <div>
                    <DotsVerticalIcon className="h-6 text-white"/>
                    </div>
                </div> 

            </div>

    {/* Post Mid */}
    <div className="flex justify-center">
        {/* Button */}
        <div className="w-full sm:w-1/2 justify-center transition delay-50 flex mt-1 py-2 px-4 items-center cursor-pointer bg-gray-700 font-bold  hover:bg-cyan-400">
            <PlusIcon className="p-0.5 h-7 text-white"/>
            <p className="text-white">Social Post</p>
        </div>

    </div>

    </div>


    )
}

export default Posts;
