import * as axs from './axs';

export const getTipsAction = (postId) => async (dispatch) => {
    try{
        const {data} = await axs.getTipsApi(postId);
        console.log(data);

        dispatch ({type: 'GET_POST_TIPS', payload: data});



    } catch(error) {
        console.log(error); 

        return false;
    } 
}


export const postTipsAction = (tipData, setpopSure, setpopTip) => async (dispatch) => {
    console.log("Tips Action Act");
    try{
        const {data} = await axs.postTipsApi(tipData); 
        const newTip = data.newTip;
        const newTipInArray = data.newTipInArray;
        const tippedPostId = data.tippedPostId; 

        const tipToPush = {tipAmount: newTipInArray, postId: tippedPostId };
        console.log(tipToPush);
        //const tipperToPush

        //dispatch ({type: 'POST_TIP', payload: data});

        if (type === 'post'){ 
            dispatch ({type: 'TIP_DIARY', payload: tipToPush});
        } 

        // setpopSure(false);
        // setpopTip(false);

    } catch(error) {  
        console.log(error);
    }
}
