import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import{faPrayingHands} from "@fortawesome/free-solid-svg-icons";

function ReviewBubble({Src,Name,Review}) {
    return (
        <div className="flex">


        <div className="flex w-full justify-start items-center px-12 bg-red-100 text-xs font-bold text-gray-700 rounded-md lg:max-w-none justify-center m-1">
            <div className="items-center">
                <img src={Src} alt="DP" className="p-0.5 rounded-full object-cover h-10 w-10 m-0.5"/>
            <div className="bg-gray-100 rounded-full flex justify-center cursor-pointer h-7 w-7 items-center hover:bg-cyan-400 group ">
                <FontAwesomeIcon icon={faPrayingHands} className="text-gray-400 group-hover:text-white" />
                </div>
                </div>
                <div className="m-1 bg-gray-300 p-3 rounded-xl max-width-xs">  
                    <p> {Name} </p>
                    <div className= "font-light break-words">{Review}</div>
                </div>

            </div>


        </div>
    )
}

export default ReviewBubble;
