import {useParams} from "react-router-dom";
import {getDiaryAction} from '../../Midwares/rdx/actions/diariesAction.js'
import{useState, useEffect} from 'react';
import {useDispatch, useSelector} from 'react-redux';
import PostFrame from '../PostFrame.jsx';
import {BeatLoader} from "react-spinners";

export const DiaryLink = ({setDiaryId}) => {

  const {diaryId} = useParams();
  const dispatch = useDispatch();

  useEffect(() => {
    if( diary && diary?.length > 0){
      dispatch(getDiaryAction(diaryId));
    }
  }, []);

  const visitedDiaries = useSelector((state) => state.visitedDiariesReducer);
  const diary = visitedDiaries.filter((diaryItem)=> diaryItem._id ===diaryId);

  return (
    <>
        {diary?.length>0 ?

            <div> 
              <PostFrame  key={diary._id} diary={diary} diaryId={diaryId} setDiaryId ={setDiaryId}/> 
            </div>  
            : 
            <div className="p-3 flex ">   
            <div className="text-center text-sm text-gray-400 p-3 m-auto rounded-xl">     
                <BeatLoader size={24} color='pink' loading/>
            </div>
            </div>  

        }
    </>
  )}
