import{GrMoney} from 'react-icons/gr';
function ProfilemodalRow ({Icon,title, walletAmount}) {
    return (
        <>

        <div className="z-30 items-center flex space-x-1 justify-start m-3 bg-transparent py-1 px-1  hover:bg-cyan-500 rounded-full cursor-pointer group">
           <div className="p-2 bg-gray-100 rounded-full items-center flex justify-center">
               {Icon && <Icon className="h-5 w-5 m-auto "/>}
            </div>
           <p className= "inline-block px-1 text-sm sm:inline-flex font-medium  group-hover:text-white">
               {title}</p> 



        </div> 

        {walletAmount && 
        <div className='w-full flex justify-center'>
        <div className= 'text-left border-l space-y-1 border-gray-300 bg-transparent rounded-md pl-4'>

            <div className= "flex items-center justify-center space-x-1 text-xs font-light text-gray-800">
                <GrMoney size={10}/>
               <p>Balance</p>
            </div> 

            <div className= "flex text-xs font-light text-gray-800">
               <p className="font-semibold"> Zoobux</p> 
               <p>: {walletAmount.zbx}</p>
            </div> 
            <p className= "text-xs font-light text-gray-800">
                Usd: {walletAmount.usd}
            </p> 
            <p className= "text-xs  font-light text-gray-800">
                Kshs: {walletAmount.kshs}
            </p> 
            <p className= "text-xs font-light text-gray-800">
                Ugx: {walletAmount.ugx}
            </p> 
            <p className= "text-xs font-light text-gray-800">
                Tzs: {walletAmount.tzs}
            </p> 

        </div>
        </div>}

      </>

    )
}

export default ProfilemodalRow;
