import { useState, useEffect } from "react";
import { MdOutlineCancel, MdSecurity } from "react-icons/md";
import {CgProfile} from "react-icons/cg";
 import { DpCropper } from "./DpCropper.jsx";

export const Settings = () => {
    const[dpCropper, setdpCropper] = useState(false);
    const[profileChanged, setProfileChanged] = useState(false);
    const[securityChanged, setSecurityChanged] = useState(false);
    const[user,setUser] = useState(JSON.parse(localStorage.getItem('profile')));
    const [profileFormData, setProfileFormData] = useState({userName:user.result.userName, bio: user.result.bio});
    const [securityFormData, setSecurityFormData] = useState({email:user.result.email, password: '', confirmPassword:''});



    const handleSubmit = () =>{ 
        console.log('submitted')
    } 


    useEffect(() => {
        if (user.result.userName === profileFormData.userName && user.result.bio === profileFormData.bio){
        setProfileChanged(false);
        } else {
            setProfileChanged(true);
        }
    }, [profileFormData.userName, profileFormData.bio ]);

  return (
      <>
       { dpCropper &&
        <DpCropper dpCropper={dpCropper} setdpCropper ={setdpCropper}/>
        }
    <div className="fixed text-gray-600 font-bold top-12 bg-transparent pt-8 pb-48 left-0 w-full flex justify-center z-20 max-h-screen overflow-y-scroll">

        {/* ============Floating Box======== */}



            <div className="w-full lg:w-2/5 bg-gray-100 rounded-md shadow-xl m-2 h-full">

            <form onSubmit={handleSubmit}>
                        <div className ='flex p-3 justify-center items-center'>

                             {/*========= Security Settings=============== */}
                            <div className= "flex items-center justify-around">
                                <CgProfile size={20} className= "text-gray-500"/>
                            </div> 
                            <div className="p-1 text-center bg-transparent">
                                    <p>Profile Settings</p>                        
                            </div>
                        </div>




                        {/*========= Profile Settings=============== */}
                    <div className="p-3 text-sm space-y-1"> 

                        <div className='flex items-center justify-left space-x-2'>
                             <p className='text-sm font-semibold text-gray-600'>Profile Picture:</p>
                            <div onClick={()=>{setdpCropper(true)}} className='flex cursor-pointer hover:bg-gray-200 border border-gray-300 justify-left p-2 rounded-md items-center bg-gray-100 space-x-1'>                              
                                    <img src={user.result.dpUrl} alt="DP" className="rounded-full h-8 w-8 sm:h-10 sm:w-10"/>                   
                                    <p>Change Picture</p>                       
                            </div>
                        </div>

                            {/* UserName*/}
                            <div className= "flex justify-between relative w-full">
                                <div className='flex justify-between items-center w-full'>
                                <p className='text-sm font-semibold text-gray-600'>Username:</p>
                                <input  onChange={(e)=> {
                                    setProfileFormData({...profileFormData, userName:e.target.value.trim()});
                                    //setProfileChanged(true);
                                }
                                }
                                value= {profileFormData.userName}
                                name='username' className= "w-3/4 sm:w-5/6 bg-gray-100 border border-gray-300 p-2 rounded-md" type="text" placeholder= "Change Username"/>
                                </div>
                            </div>


                            {/*Bio*/}
                            <div className= "flex justify-between relative w-full pb-2">
                                <div className='flex justify-between items-center w-full'>
                                <p className='text-sm font-semibold text-gray-600'>Bio:</p>
                                <textarea onChange={(e)=>{
                                    setProfileFormData({...profileFormData, bio:e.target.value.trim()});
                                    //setProfileChanged(true);
                                }
                                } value= {profileFormData.bio}
                                name='bio' className= " resize-none w-3/4 sm:w-5/6 bg-gray-100 border border-gray-300 p-2 rounded-md" type="text" placeholder= "Change Bio"/>
                                </div>
                            </div>
                    </div>

                    <div className='flex justify-between'>
                            {profileChanged ===true && <button 


                            type='submit' className=" flex items-center px-4 py-3 mx-auto bg-gradient-to-r from-cyan-300 to-cyan-500 
                                bg-gradient-to-r hover:from-pink-500
                                hover:to-yellow-500 flex
                                mx-auto rounded-full
                                    justify-center 
                                    text-white text-sm cursor-pointer
                                    font-semibold p-1 mb-4 space-x-1">
                                     <CgProfile size={20} className= "text-gray-100"/>

                                <p>Submit Profile Changes</p> 

                            </button>}


                            {profileChanged === false && <button 


                            type='submit' className=" flex items-center px-4 py-3 mx-auto border border-gray-300 text-gray-400 
                              flex
                                mx-auto rounded-full
                                    justify-center 
                                    text-white text-sm cursor-pointer
                                    font-semibold p-1 mb-4 space-x-1">
                                     <CgProfile  size={20} className= ""/>

                                <p>Submit Profile Changes</p> 

                            </button>}



                    </div>

            </form>

            <form onSubmit={handleSubmit}>



                     {/*========= Security Settings=============== */}
                       <div className ='flex p-3 justify-center items-center'>

                             {/*========= Security Settings=============== */}
                            <div className= "flex items-center justify-around">
                                <MdSecurity size={20} className= "text-gray-500"/>
                            </div> 
                            <div className="p-1 text-center bg-transparent">
                                    <p>Security Settings</p>                        
                            </div>
                        </div>


                     {/*========= Security Settings Inputs=============== */}
                    <div className="p-3 text-sm space-y-1">

                            {/* Email*/}
                            <div className= "flex justify-between relative w-full">
                                <div className='flex justify-between items-center w-full'>
                                <p className='text-sm font-semibold text-gray-600'>Email:</p>
                                <input 
                                value= {securityFormData.email}
                                onChange={(e)=>{
                                    setSecurityFormData({...securityFormData, email:e.target.value});
                                    setSecurityChanged(true);
                                }
                                }
                                value= {profileFormData.email}
                                name='email' className= "w-3/4 bg-gray-100 border border-gray-300 p-2 rounded-md" type="text" placeholder= "Change Email"/>
                                </div>
                            </div>

                            {/* Password*/}
                            <div className= "flex justify-between relative w-full">
                                <div className='flex justify-between items-center w-full'>
                                <p className='text-sm font-semibold text-gray-600'>Change Password:</p>
                                <input  onChange={(e)=>{
                                    setSecurityFormData({...securityFormData, password:e.target.value});
                                    setSecurityChanged(true);
                                }
                                }
                                name='password' className= "w-3/4 bg-gray-100 border border-gray-300 p-2 rounded-md" type="password" placeholder= "Change Password"/>
                                </div>
                            </div>


                            {/* ConfirmPassword*/}
                             <div className= "flex justify-between relative w-full">
                                <div className='flex justify-between items-center w-full'>
                                <p className='text-sm font-semibold text-gray-600'>Confirm Password:</p>
                                <input  onChange={(e)=>{
                                    setSecurityFormData({...securityFormData, confirmPassword:e.target.value});
                                    setSecurityChanged(true);
                                }
                                }
