                       {loading ===true &&
                       <div>
                           <BeatLoader size={20} color='white' loading/>
                       </div>}
                       {

                       }

                        {loading ===false && 
                        <div>
                            <div className='cursor-pointer p-1  hover:text-gray-700'>
                                <p>People Are Good bkhbkhjbjhk buhbkbhhku huhihhiuhiu huhihiuh</p>  
                            </div>
