import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import{faPrayingHands} from "@fortawesome/free-solid-svg-icons";

function ReviewBubble({Src,Name,Review}) {
    return (


            // Overall
        <div className="flex w-full justify-start items-center text-xs font-bold text-gray-700 rounded-md lg:max-w-none">

           {/* EMoji & Pic */}
            <div className="space-y-3 items-center inline-block p-1">
                <img src={Src} alt="DP" className="rounded-full object-cover h-8 w-8"/>
                <div className="bg-gray-100 rounded-full flex justify-center cursor-pointer h-7 w-7 items-center hover:bg-cyan-400 group ">
                    <FontAwesomeIcon icon={faPrayingHands} className="text-gray-400 group-hover:text-white" />
                </div>
            </div>
            {/* Name and Comment*/}
            <div className="flex m-1 bg-transparent w-4/5">  
               <div className="bg-gray-300 p-3 rounded-2xl max-w-sm">
                    <p> {Name} </p>
                <div className= "font-normal break-words">{Review}</div>
                </div>
            </div>

        </div>



    )
}

export default ReviewBubble;
