import { CameraIcon} from "@heroicons/react/solid";
import{useState} from 'react';
import FileBase from 'react-file-base64';
import{BsFacebook, BsInstagram} from "react-icons/bs";
import{ImInstagram, ImYoutube2} from "react-icons/im";
import {useDispatch} from 'react-redux'; 
import { postDiariesAction } from "../Midwares/rdx/actions/diariesAction.js";
import PostFRow from "./PostFRow.jsx";

import axios from 'axios';

function PostForm() {

    const [diariesData, setdiariesData] = useState({
        title:'', caption:'', file: '', publicity:'',
    }); 
    const dispatch = useDispatch();

    const handleSubmit =(e)=>{
        e.preventDefault();


        //dispatch(postDiariesAction (diariesData)); 
    }
    return (
        <div className="flex items-center justify-center">
                <div className="space-y-5 w-full xl:w-2/5 bg-transparent items-center  z-40  m-4">


                        {/* Cyan Heading */}
                        <div className="bg-transparent border-b-2 border-gray-300">
                            <p className="text-center p-3 font-bold text-gray-400 ">Attach Post From</p>
                            <div className ="text-center  items-center p-2 flex flex-wrap text-xs text-gray-400">
                               <PostFRow sizing={50}  platform="youtube" Icon={ImYoutube2}/>
                               <PostFRow sizing ={34} Icon={BsInstagram}/>

                                <div>
                                    <p>Facebook</p>
                                </div>
                                <div>
                                    <p>Tiktok</p>
                                </div>
                                <div>
                                    <p>Twitter</p>
                                </div>
                                <div>
                                    <p>Soundcloud</p>
                                </div>
                                <div>
                                    <p>Pinterest</p>
                                </div>
                                <div>
                                    <p>Reddit</p>
                                </div>
                                <div>
                                    <p>Twitch</p>
                                </div>
                                <div>
                                    <p>Wordpress</p>
                                </div>

                            </div>
                        </div>

                    {/*----- FORM------------------------- */}
                    <form onSubmit={handleSubmit}>
                        <div className= "flex justify-center items-center p-0.5">
                            <img src="./assets/images/milome.jpeg" alt="DP" className="rounded-full h-7 w-7"/>

                        <select 
                        name= "publicity"
                        value= {diariesData.publicity}
                        onChange={(e)=> setdiariesData({...diariesData, publicity: e.target.value})}

                        className="m-2 flex justify-center items-center font-semibold text-xs text-gray-500 outline-none bg-gray-100 rounded-md p-1 border-none">
                            <option value="public"> Public/ Everyone </option>
                            <option value="subscribers">My Subscribers </option>
                            <option value="private"> Private/ Only Me </option>
                        </select> 
                        </div>
                        <div className="">


                            {/* File Upload------------ */}

                                    {/* <div className= "items-center bg-gray-100 rounded-full p-3 text-gray-300 mx-auto w-20 h-20">

                                    <FileBase

                                        type="file"
                                        multiple={false}
                                        onDone={({base64})=> setdiariesData ({...diariesData, file:base64})}

                                    />
                                        <CameraIcon className="m-auto h-10 w-10"/>
                                        <p className= "text-center text-xs font-base text-gray-400">Photo</p>
                                    </div> */}

                                {/*-- Title------------ */}
                                    <div className="flex justify-center">
                                        <input name= "Post Url"
                                        value= {diariesData.url}
                                        onChange={(e)=> setdiariesData({...diariesData, url: e.target.value})}
                                        placeholder="Paste Url Here" className="text-center text-gray-700 font-bold outline-none  mx-4 my-3 w-full px-4 p-1 sm:py-2 border-b-2 border-gray-300 bg-gray-100"/>
                                    </div>
                                        {/*-- Title------------ */}
                                    <div className="flex justify-center">
                                        <input name= "title"
                                        value= {diariesData.title}
                                        onChange={(e)=> setdiariesData({...diariesData, title: e.target.value})}
                                        placeholder="Enter Title" className="text-center text-gray-700 font-medium outline-none  mx-4 my-3 w-full px-4 p-1 sm:py-2 rounded-full bg-gray-100"/>
                                    </div>
                                {/* ---Content---------------  */}
                                    <div className="px-3 items-center flex justify-center">
                                        <textarea name= "caption"
                                        value= {diariesData.caption}
                                        onChange={(e)=> setdiariesData({...diariesData, caption: e.target.value})}
                                        placeholder=" Enter Caption" className="resize-none h-28 sm:h-32 text-gray-700 font-light outline-none  m-1 w-full bg-gray-100 px-4 py-2 rounded-md"/>
                                    </div>

                                {/* Button------------- */}
                                    <button type='submit' className="items-center mx-auto bg-gradient-to-r from-pink-300 to-cyan-400 
                                    bg-gradient-to-r hover:from-pink-500
                                    hover:to-yellow-500 my-3 flex
                                    mx-auto w-1/3 rounded-full
                                        my-2 justify-center 
                                        text-white cursor-pointer
                                        font-semibold p-1">
                                        Preview
                                    </button>





                        </div>
                        </form>
        </div>
        </div>
    )
}

export default PostForm;
