export const notificationsReducer = (notifications = [], action) => {

    switch(action.type) {
        case 'POST_NOTIFICATIONS': 
            return notifications;
        case 'GET_NOTIFICATIONS': 
            return action.payload;
        case 'READ_NOTIFICATIONS': 
            notifications.filter((notification)=> notification._id !== action.payload._id && notification.read === false);
            // notifications.map((notification) => notification._id === action.payload._id ? action.payload : notification.read);
            return [...notifications, action.payload];       
        case 'SOCKET_GOT_NOTIFICATION':    
            return [...notifications, action.payload];
        default:
            return notifications; 
     }

 }
