import { useSelector } from 'react-redux'

export function GetSocket() {
  return useSelector((state) => state.socketReducer);
} 

const socket = GetSocket ();


  export const messageSocket = (messageData) =>{
    socket.current.emit("sendMessage", {
      messageData
    });
  }
  export const notificationSocket = (socketNotificationData) =>{
    socket.current.emit("socketNotificationData", {
      socketNotificationData
    });
  }
