import {useSelector, useDispatch} from 'react-redux';


export const ReviewTipRow = ()=>{

    const reviewTippersAll = useSelector((state) => state.tipsReducer);
    const availableReviewTippers = reviewTippersAll.filter(tipper => tipper.tippededPostId === reviewId);

    const dispatch = useDispatch();

    useEffect(() => {     

        if(!availableReviewTippers.length){

            dispatch(getTipsAction(diaryId));    
        }

    }, [dispatch]); 

    return(
        <>
        {availableReviewTippers.length > 0  && availableReviewTippers.map((reviewTipper) =>(
        <div className= ' break-words p-1 border border-gray-300 bg-gray-100 hover:bg-white'>
             <p>{reviewTipper.tipperMiniProfile.userName}: {reviewTipper.amount}</p>    
        </div>  
          ))

        } 
        </>
    )
}
