import Skeleton, { SkeletonTheme } from 'react-loading-skeleton'
import 'react-loading-skeleton/dist/skeleton.css'
import{useState} from 'react';

export const SkeletonPostRow=()=> {
    const[mode, setMode] = useState(null);

    const modeSetter =()=> {
        if (
            //localStorage.theme === 'dark' || (!('theme' in localStorage) &&       //localstorage.createItem use this 
        window.matchMedia('(prefers-color-scheme: dark)').matches
        //)
        ) {
            //document.documentElement.classList.add('dark') //work on it later
            setMode('dark'); 
        } else {
            //document.documentElement.classList.remove('dark')
            setMode('dark');
        } 
    }
  return (


    <div className='w-full items-center'>

         <SkeletonTheme baseColor={mode === 'dark' ? "#606060" : "#ffffff"} highlightColor={mode === 'dark' ? "#303030" : "#dcdfe3"}>
        <div className='mx-auto space-y-2 text-black px-8 sm:px-16 py-8 rounded-md sm:rounded-xl bg-gray-100 dark:bg-gray-800 relative w-full sm:w-3/4 xl:w-2/5 mx-auto mb-6 bg-gray-100'>

            <div className ='flex  space-x-4 items-center'>
                <div>
                    <Skeleton circle width={40} height={40}/>
                </div>

                <div className= 'items-center'>
                    <Skeleton width={180} height={20}/>
                    <Skeleton width={180} height={20}/>
                </div>
            </div>

            <div>
                <Skeleton height={120}/>
            </div>
            <div className='pb-4'>
                <Skeleton height={40}/>
            </div>

        </div>
        </SkeletonTheme>

    </div>



  )
}

