import { useState } from 'react';

export const DpCropper = ({setdpCropper}) => {

    const[dpPreview, setdpPreview] = useState('none');
    const[user,setUser] = useState(JSON.parse(localStorage.getItem('profile')));



    const handleImage = async (e)=>{

        e.preventDefault();
        const image = e.target.files[0];

        setdpPreview (image);
        console.log(image);
        console.log(dpPreview);



    };


    return (
        <div className= "fixed w-full h-full z-40 left-0 top-0 bg-blue-200 ">
            <div className= "w-full h-full bg-yellow-200 flex">
                <div className="m-auto bg-red-100 flex justify-center ">
                    <div>
                        <div className="flex justify-center">
                        {dpPreview=='none' ?
                        <img src={user.result.dpUrl} alt="DP" className="p-0.5 rounded-full h-80 w-80"/> :
                        <img src={dpPreview} alt="DP" className="p-0.5 rounded-full h-80 w-80"/>
                        }
                        </div>
                        <div className="flex justify-center space-x-3">



                            <input onChange={handleImage} className= "hidden" id='dpUpload' type="file"/>
                            <label for= 'dpUpload'>
                                <div  className="p-2 font-semibold text-white rounded-md bg-gray-700 items-center flex">
                                    Upload
                                </div>
                            </label>

                            <div className="p-2 font-semibold text-white rounded-md bg-gray-700 items-center flex">
                                Submit
                            </div>

                        </div>
                    </div>
                </div> 
            </div>

        </div>
    )
}
