import * as axs from './axs';

export const getMessagesAction = (convoId) => async (dispatch) => {
    try{
        const {data} = await axs.getConvosApi(convoId);

        dispatch ({type: 'GET_ALL_MESSAGES', payload: data});
    } catch(error) {
        console.log(error);
    }
}


export const postConvosAction = (messageData) => async (dispatch) => {
    try{
        const {data} = await axs.postConvosApi(messageData);

        dispatch ({type: 'POST_MESSAGE', payload: data});
    } catch(error) {  
        console.log(error);
    }
}
