import {SearchIcon} from '@heroicons/react/outline';
import { useState } from 'react';
import {BsPeople, BsFillFileEarmarkPostFill} from 'react-icons/bs';
import OutsideClickHandler from 'react-outside-click-handler';


export const Search = () => {
 const [searchOptions, setSearchOptions] = useState(false);


  return (

    <div className='flex items-center'>

        <div onClick={()=> setSearchOptions(true)} className="p-1 sm:p-3 bg-cyan-400 hover:bg-cyan-600 rounded-full items:center">
            <SearchIcon  className= 'h-6 text-white'/>

        </div>
        <input className ="bg-transarent w-full h-7 md:inline-flex pl-2 pr-4 bg-transparent items-center outline-none font-light placeholder-gray-400"
        type="text"
        placeholder="Search Zoorura"/>

    {searchOptions &&
        <>
             {/* <OutsideClickHandler     
                    onOutsideClick={() => {
                        setSearchOptions(false);
                    }}> */}
                <div className= 'flex items-center fixed  top-24 sm:top-16 text-sm text-gray-400 bg-gray-200 z-40 h-fit w-1/3 rounded-md p-0.5'>

                    <div className= 'flex items-center m-1 space-x-2 px-3 py-1 rounded hover:shadow-md hover:bg-white bg-gray-100 cursor-pointer'>
                        <div><BsPeople size={20}/></div>
                        <p>Search People</p>      
                    </div>
                    <div className= 'flex items-center m-1 space-x-2 px-3 py-1 rounded hover:shadow-md hover:bg-white bg-gray-100 cursor-pointer'>
                        <div><BsFillFileEarmarkPostFill size={20}/></div>
                        <p>Search Posts</p>      
                    </div>


                </div>
                {/* </OutsideClickHandler> */}

        </>

        }



    </div>

  )
}
