import { useState } from 'react';

export const DpCropper = ({setdpCropper}) => {

    const[dpPreview, setdpPreview] = useState('none');
    const[dpPreviewUrl, setdpPreviewUrl] = useState('none');
    const[user,setUser] = useState(JSON.parse(localStorage.getItem('profile')));



    const handleImage = async (e)=>{

        e.preventDefault();
        const image = e.target.files[0];

        setdpPreview (image);
        setdpPreviewUrl (URL.createObjectURL(image));
        console.log(image);
        console.log(dpPreview);
        console.log(dpPreviewUrl);



    };


    return (
        <div className= "fixed w-full h-full z-40 left-0 top-0 bg-transparent ">
            <div className= "w-full h-full bg-gray-700 opacity-90 flex">
                <div className="p-3 m-auto bg-black flex justify-center ">
                    <div>
                        <div className="flex justify-center">
                            {dpPreview=='none' ?
                            <img src={user.result.dpUrl} alt="DP" className="p-0.5 rounded-full h-96 w-96 object-contain"/> :
                            <img src={dpPreviewUrl} alt="DP0" className="p-0.5 h-96 w-100 object-contain"/>
                            }
                        </div>
                        <div className="flex justify-center space-x-3">



                            <input onChange={handleImage} className= "hidden" id='dpUpload' type="file"/>
                            <label for= 'dpUpload'>
                                <div  className="p-2 font-semibold text-white rounded-md bg-gray-700 items-center flex">
                                    Upload
                                </div>
                            </label>

                            <div className="p-2 font-semibold text-white rounded-md bg-gray-700 items-center flex">
                                Submit
                            </div>

                        </div>
                    </div>
                </div> 
            </div>

        </div>
    )
}
