import{useState, useEffect } from 'react';

import {HiOutlineChatAlt2} from "react-icons/hi"



function ConvoForm({miniProfile}) {




    const handleSubmit = async (e)=>{
        e.preventDefault();

            try{
                console.log('diariesData');

            }
            catch(err){

                console.log(err)

            }

    }
    return (
        <div className="w-96 bg-gray-100 flex items-center justify-center">

                {/* { popPosted &&
                        <div className=" bg-gray-700 py-4 top-28 rounded-full px-20 flex justify-center fixed z-40 m-auto text-center font-bold text-white">
                           <p> Post Updated! </p>
                        </div>
                    } */}

                <div className="space-y-5 w-full bg-transparent items-center  z-30  m-4">



                        {/* Cyan Heading */}
                        <div className="bg-transparent border-b-2 items-center border-gray-300">
                            <div className= "flex justify-center">
                            <HiOutlineChatAlt2 size={20} />
                            </div>
                            <p className="text-center p-3 font-bold text-gray-400 ">Create Private Chat Room with @{miniProfile.userName}</p>

                        </div>

                    {/*----- FORM------------------------- */}
                    <form onSubmit={handleSubmit}>

                        <div className= "flex justify-center items-center p-0.5">
                            <img src={miniProfile.dpUrl} alt="DP" className="rounded-full h-10 w-10"/>
                        </div>

                        <div className="">


                                       {/*-- Title------------ */}
                                       <div className="flex justify-center">
                                        <input name= "title"

                                        placeholder="Enter Title" className="text-gray-700 font-medium outline-none  mx-4 my-3 w-full px-4 p-1 sm:py-2 border-2 border-gray-300 rounded-md bg-gray-200"/>
                                    </div>
                                {/* ---Content---------------  */}
                                    <div className="px-3 items-center flex justify-center">
                                        <textarea name= "caption"

                                        placeholder=" Enter Caption" className="resize-none h-14 sm:h-24 text-gray-700 font-light outline-none  m-1 w-full  px-4 py-2 border-2 border-gray-300 rounded-md bg-gray-200"/>
                                    </div>







                                {/* Button------------- */}
                                    <button type='submit' className="items-center mx-auto bg-gradient-to-r from-pink-300 to-cyan-400 
                                    bg-gradient-to-r hover:from-pink-500
                                    hover:to-yellow-500 my-3 flex
                                    mx-auto w-1/3 rounded-full
                                        my-2 justify-center 
                                        text-white cursor-pointer
                                        font-semibold p-1">
                                       Edit Post
                                    </button>





                        </div>
                        </form>
        </div>
        </div>
    )
}

export default ConvoForm;
