import LeftbarRow from './LeftbarRow';
import {useSelector} from 'react-redux';
import {BeatLoader} from "react-spinners";
import {Link} from 'react-router-dom';


function LeftbarMob(){

    const hallFame = useSelector((state) => state.hallFameReducer);
    console.log(hallFame); 
    // text

return (
    <div className="rounded-xl visible xs:invisible p-2 w-full m-x-4 bg-gray-200 border-r border-gray-300 z-30 fixed left-0 top-24 h-screen  overflow-y-auto overflow-x-hidden">
        <div className="overflow-scroll">
            {/* space */}
        <div className= "h-16">
            <p></p>
        </div>
            {/* Heading */}
            <div className="bg-transparent border-b hover:from-pink-500 hover:to-yellow-500 fixed top-16 left-0 my-2 p-3 cursor-pointer  w-1/4 flex justify-center">
                <p className= "text-sm font-semibold"># Hall of Fame Ranking</p>

            </div> 

            {/* Hall OF FAME */}
            <div className="mb-60">
                { !hallFame.length ?
                    <div className="p-3 flex">   
                      <div className="text-center text-sm text-gray-400 p-3 m-auto bg-transparent rounded-xl">

                          <BeatLoader size={15} color='cyan' loading/>
                          <p className= 'text-xs'>Preparing Hall Of Fame Ranks...</p>

                      </div>
                  </div>  
                  :

                    <>
                    { hallFame.map((rank) =>(
                        <Link to={'/Portfolios/' + rank.userName} key={rank._id}>
                             <LeftbarRow Src = {rank.dpUrl} title ={(hallFame.indexOf(rank)+1)} PersonName= {rank.userName} Points= {rank.activityPointsTotal}/>
                        </Link>
                        ))
                    } 
                    </>

        }
            </div>
    </div>
</div>
);
}
export default LeftbarMob;




