import * as axs from './axs';

export const changeDpAction = (dp) => async (dispatch, navigate) => {
    try{
        const {data} = await axs.changeDpApi(dp); 
        dispatch ({type: 'CHANGE_DP', data});

        navigate('/Portfolios');
        window.location.reload(true);
    } catch(error) {
        console.log(error);
    }
}
export const getMiniProfileAction = (id, miniProfile) => async (dispatch) => {

    if(
         miniProfile.filter(({ _id }) => _id === id) == true
    )
    {
        try{
            const {data} = await axs.getMiniProfileApi(id); 
            dispatch ({type: 'GET_MINI_PROFILE', data});

        } catch(error) {
            console.log(error);
        }
    }
    else{
        console.log('already');
    }
}
