import { DotsVerticalIcon} from "@heroicons/react/outline";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import{faHandPointUp, faComments, faShareSquare, faPrayingHands} from "@fortawesome/free-solid-svg-icons";
import TipModal from '../Modals/TipModal.jsx'
import { useState } from 'react';
import OutsideClickHandler from 'react-outside-click-handler';
function PostBox() {

    const[popTip, setpopTip] = useState(false);

    return (

        // Post-Box
        <div className="p-2 sm:p-0 rounded-xl bg-white relative sm:w-1/2 mx-auto my-1"> 

            {/* Post-Top-Cyan Invisible Parent*/}
            <div className="flex  justify-center">
                {/* Top-Cyan */}
                <div className="bg-gradient-to-r from-teal-300 to-blue-400 rounded-l-full w-full rounded-t-md transition delay-50 py-0.5 flex items-center font-bold shadow-xl justify-between">
                        {/*Top-Mid*/}
                        <div className="flex items-center justify-between">
                            {/* Top-Mid Img*/}
                            <div className="ml-3 bg-white rounded-full">
                                <img src="./assets/images/milome.jpeg" alt="DP" className=" rounded-full h-10 w-10 m-0.5"/>
                            </div> 
                            {/*Top-Mid-Words */}
                            <div className="sm:ml-2 items-center ml-0.5 py-3"> 
                                <p className="leading-3 text-sm font-medium my-1 ">@FelixMilome #3</p>
                                <p className="leading-3 text-xs font-extralight my-1">Social Post: 50 Mins ago</p>

                            </div>
                        </div>
                    {/* Top-Menu */}
                    <div>
                    <DotsVerticalIcon className=" h-7 pr-3 items-center cursor-pointer hover:text-cyan-300 text-white"/>
                    </div>

                </div> 

            </div>

        {/* Post Caption Invisible Parent */}
        <div className="flex justify-center  my-0.5">
            {/* Post Mid Frame*/}
            <div className="w-full items-center p-5 bg-transparent rounded-t-xl"> 
                                <p className="leading-5 text-base font-medium my-1 text-gray-500">How I became a Billionaire</p>
                                <p className="leading-5 text-sm font-light text-gray-700">I was born to become one but I also worked for it. Always stay focussed and positive for one day all will go well for you. Never give up in whatever you are pursuing</p>                 
            </div>  
        </div>

        {/* Post Mid Invisible Parent */}
        <div className="flex justify-center">
            {/* Post Mid Frame*/}
            <div className="w-fulltransition delay-50 flex p-1 items-center cursor-pointer bg-gray-300 font-bold hover:bg-cyan-400">
                <img src="./assets/images/khabilame.jpeg" alt="DP" className="h-full w-full"/>  
            </div>     
        </div>

        {/*Bottom Icon invisible Parent*/}
        <div className="flex justify-center">
            {/* Post Bottom Icons*/}
            <div className="w-full  justify-around transition delay-50 flex items-center  bg-transparent border-b-2 border-gray-300 font-bold p-3">
            <OutsideClickHandler onOutsideClick={() => {setpopTip(false);}}>
            {popTip && <TipModal />}

                    <div className="relative flex items-center rounded-full p-1 cursor-pointer bg-gradient-to-r hover:bg-cyan-100 hover:from-blue-100 hover:to-green-100"
                        onClick={ () => {setpopTip(!popTip)}}>

                        <FontAwesomeIcon icon={faPrayingHands} size ={'2x'} className="text-cyan-400 p-1" />
                        <p className="font-light text-xs m-1 text-gray-800">30M Claptips</p>

                    </div>   
                    </OutsideClickHandler>
                {/* <OutsideClickHandler onOutsideClick={() => {setpopTip(false);}}> */}

                {/* </OutsideClickHandler> */}

                {/* Other Icon */}
                <div className="flex items-center rounded-full p-1 cursor-pointer hover:bg-blue-100">
                    <FontAwesomeIcon icon={faComments} size ={'2x'} className="text-blue-400 p-1.5"/>
                    <p className="font-light text-xs m-1 text-gray-800">500K Reviews</p>
                </div>
                <div className="flex items-center p-1 rounded-full cursor-pointer hover:bg-green-100">
                    <FontAwesomeIcon icon={faShareSquare} size ={'2x'} className="text-green-400 p-1.5"/>
                    <p className="font-light text-xs m-1 text-gray-800">400M Displays</p>
                </div>
            </div>     
        </div>
    </div>
    )
}

export default PostBox;
