import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import{faPrayingHands} from "@fortawesome/free-solid-svg-icons";

function ReviewBubble({Src,Name,Review}) {
    return (
        <div className="flex max-w-sm">


        <div className="flex w-full bg-red-100 justify-start items-center p-2 bg-transparent text-xs font-bold text-gray-700 rounded-md">
            <img src={Src} alt="DP" className="p-0.5 rounded-full object-cover h-10 w-10 m-0.5"/>
                <div className="m-1 bg-gray-300 p-3 rounded-xl max-w-sm">  
                    <p> {Name} </p>
                    <p className= "font-light">{Review}</p>
                </div>
                <div className="bg-transparent rounded-full flex justify-center cursor-pointer h-7 w-7 items-center hover:bg-cyan-400 group">
                <FontAwesomeIcon icon={faPrayingHands} className="text-gray-400 group-hover:text-white" />
                </div>
            </div>


        </div>
    )
}

export default ReviewBubble;
