import {HiOutlineChatAlt2} from "react-icons/hi"
import {quickFollowAction} from "../Midwares/rdx/actions/profileAction.js";
import {BeatLoader} from "react-spinners";
import { useDispatch, useSelector} from "react-redux";
import { useState } from "react";
import ContactMod from '../Modals/ContactMod.jsx';

export const ChatHuntRow = ({miniProfile, setpopContactsFalse}) => {

    const[loadingFollow, setLoadingFollow] = useState(false);
    const [contactsIndi, setContactsIndi] = useState(false);
    const [popConvoForm, setpopConvoForm] = useState(false);

    const user = JSON.parse(localStorage.getItem('profile'));

    const convosAll = useSelector((state) => state.convosReducer);
    const convoState = useSelector((state) => state.convoStateReducer); 



    const dispatch = useDispatch ();

    const  Chat = convosAll.filter( Chat =>

        ( Chat.guest._id == user.result._id &&  Chat.host._id === miniProfile._id)
        ||( Chat.host._id == user.result._id &&  Chat.guest._id === miniProfile._id)

    );

    const followHandler= (userId) => {
        const followDataObj = {follower:'', followed:userId}; 
        setLoadingFollow(true);
        dispatch (quickFollowAction(followDataObj));
    }

    //SetOnlineChecker for each Box. Then a shuffle button.


    const convoHandler = async() =>{ 

        if( Chat.length < 1 && convoState !== 'SearchingConvo'){

            setpopConvoForm(true);

        }
        else if ( Chat.length > 0 && convoState === 'YesConvo'){
            setpopConvoForm(true);
           setpopContactsFalse();
            setContactsIndi(true);

        }
    }

  return (
        <>

            <div className= 'z-30 m-auto my-2 flex justify-center items-center rounded-xl h-80 p-2  text-xs w-5/6 bg-gray-100'>
                <div className='m-auto'>

                    <img src={miniProfile.dpUrl} alt="DP" className="m-auto p-0.5 rounded-full h-30 w-30 "/>

                    <div className='m-auto w-32 break-words my-1 text-center font-bold'>
                        <p>@{miniProfile.userName}</p>
                    </div>
                    <div  className='flex justify-center mx-auto flex items-center text-center'>
                            <p className='text-red-500'>offline</p>
                        </div>

                    <div className='space-y-2 my-2'>
                        {loadingFollow ===false &&
                        <div onClick={()=>followHandler(miniProfile._id)} className='flex justify-center mx-auto text-center font-bold text-white bg-gray-800 hover:bg-cyan-400 rounded  p-1 cursor-pointer'>
                            <p>subscribe</p>
                        </div>}
                        {loadingFollow === true &&
                        <div className='flex justify-center mx-auto text-center font-bold text-white bg-gray-800 hover:bg-cyan-400 rounded  p-1 cursor-pointer'>
                           <BeatLoader size={5} color ='cyan'/>
                        </div>}
                        <div onClick={convoHandler}  className='flex justify-center mx-auto flex items-center text-center   border border-gray-800 hover:bg-white rounded py-1 px-2 cursor-pointer'>
                            <HiOutlineChatAlt2 className=" h-4 w-4 text-gray-700"/> 
                            <p>Start Chat</p>
                            <p className='text-red-500'>: offline</p>
                        </div>


                    </div>



                </div>
            </div> 
            {contactsIndi && Chat[0]?._id && <ContactMod convoId={ Chat[0]?._id} setpopChatBox={setContactsIndi} displayed= {miniProfile} viewer = {user.result}/>}
        </>
  )
}
