import PostBox from "./PostBox";
import {BeatLoader} from "react-spinners";
import {useSelector} from 'react-redux';


import IGbox from './IGbox.jsx'
import Portfolio from "./Portfolio";
import { Test } from "./test";


function Portfolios(diaryId, setDiaryId) { 

const user = JSON.parse(localStorage.getItem('profile'));
const userName = user.result.userName;
const diaries = useSelector((state) => state.diariesReducer);

console.log(diaries);

    return ( 
       <div>


            { !diaries.length ? 

            <div className="p-3 flex">   
                <div className="text-center text-sm text-gray-400 p-3 m-auto bg-transparent rounded-xl">

                    <BeatLoader size={24} color='pink' loading/>
                    <p className= 'text-sm'>fetching posts..</p>

                </div>
            </div>

            : (
                <div >
                    {/* portbox */}

                        <div className='w-full opacity-90 fixed top-24  m-auto z-30  bg-transparent'>
                            <div className= 'flex m-auto bg-transparent'>
                            <div className="bg-gray-100 text-gray-700 flex m-auto justify-around text-center items-center p-2 ">

                                    {/* PIC frame */}
                                    <div className='rounded-md'>
                                            <div className= " cursor-pointer mx-3 space-y-2 rounded-xl text-xs  bg-transparent items-center mt-4 mb-3 group">
                                                <img src="./assets/images/milome.jpeg" alt="DP" className="mx-auto rounded-full group-hover:text-white h-8 w-8"/>

                                            <div className="bg-gray-100 rounded-md items-center"> 
                                            <p className= "text-gray-600 leading-3 text-center  font-bold ">@{userName}</p>
                                                <p className= "text-gray-500 leading-4 text-center font-light">Rank: #3</p> 
                                                <p className= "text-gray-500 leading-3 text-center font-semibold">30b aps</p>
                                            </div>

                                         </div> 


                                    </div>


                                    <div className='w-full'>
                                        {/* Follows */}
                                        <div className=' space-x-2  text-xs lg:space-x-8 flex justify-between items-center py-2'>
                                            <div className='leading-4'>
                                                <p className= 'font-bold '>#3</p>
                                                <p className= 'font-light '>Rank</p>
                                            </div>
                                            <div className='leading-4'>
                                                <p className= 'font-bold '>3000</p>
                                                <p className= 'font-light '>Posts</p>
                                            </div>
                                            <div className='leading-4'>
                                                <p className= 'font-bold '>1.9B</p>
                                                <p className= 'font-light'>Followers</p>
                                            </div>
                                            <div className='leading-4'>
                                                <p className= 'font-bold '>1</p>
                                                <p className= 'font-light '>Following</p>
                                            </div>

                                        </div>

                                        {/* Buttons */}
                                        <div className= 'flex justify-center items-center'>
                                            <div className="m-1 bg-cyan-500 rounded-md items-center p-2"> 
                                                <p className= "text-gray-100 leading-4 text-center font-bold">Follow</p> 
                                            </div>
                                            <div className="m-1 bg-cyan-500 rounded-md items-center p-2"> 
                                                <p className= "text-gray-100 leading-4 text-center font-bold">Inbox</p> 
                                            </div>
                                         </div>
                                    </div>
                            </div>
                            </div>




                    </div>


                    {/* Diaries */}
                    <div className='mt-32 lg:mt-40'>

                    {

                    diaries.map((diary) =>(

                        // <Test key={diary._id} diary={diary} diaryId={diaryId} setDiaryId ={setDiaryId}/>

                      <Portfolio key={diary._id} diary={diary} diaryId={diaryId} setDiaryId ={setDiaryId}/>
                        // <PostBox key={diary._id} diary={diary} setDiaryId ={setDiaryId}/>
                    ))
                    }
                    </div>
                </div>
            )
            }


           {/* <IGbox/> */}



           </div>






    )
}

export default Portfolios;
